def uploadFile1(path):
    """
    上传文件并返回其 base64 编码
    :param path: 文件路径
    :return: 字典，包含上传结果
    """
    imageText = get_file_content_as_base64(path, True)
    if not imageText:
        return {
            "code": -1,
            "message": "上传失败",
            "data": ""
        }
    return {
        "code": 0,
        "message": "上传成功",
        "data": imageText
    }


def convertToText(image_base64):
    url = "https://aip.baidubce.com/rest/2.0/ocr/v1/accurate_basic?access_token=" + get_access_token()

    payload = "image=" + image_base64
    headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Accept': 'application/json'
    }

    response = requests.post(url, headers=headers, data=payload)

    if response.status_code != 200:
        return {
            "code": -1,
            "message": "转换失败",
            "data": response.text
        }

    # 正确解析 JSON 响应
    try:
        result = response.json()
        return {
            "code": 0,
            "message": "转换成功",
            "data": result
        }
    except json.JSONDecodeError:
        return {
            "code": -1,
            "message": "JSON 解析失败",
            "data": response.text
        }

def extract_text(data):
    # 提取 words_result 列表
    words_list = data["data"]["words_result"]

    # 使用列表推导式提取所有 words 字段的值，并连接成一段话
    combined_words = ''.join([item["words"] for item in words_list])
    if not combined_words:
        return jsonify({
            "code":-1,
            "message":"转化失败",
            "data":""
        })
    return jsonify({
        "code": 0,
        "message": "转化成功",
        "data": combined_words
    })



def get_file_content_as_base64(path, urlencoded=False):
    """
    获取文件base64编码
    :param path: 文件路径
    :param urlencoded: 是否对结果进行urlencoded
    :return: base64编码信息
    """
    with open(path, "rb") as f:
        content = base64.b64encode(f.read()).decode("utf8")
        if urlencoded:
            content = urllib.parse.quote_plus(content)
    return content


def get_access_token():
    """
    使用 AK，SK 生成鉴权签名（Access Token）
    :return: access_token，或是None(如果错误)
    """
    url = "https://aip.baidubce.com/oauth/2.0/token"
    params = {"grant_type": "client_credentials", "client_id": API_KEY, "client_secret": SECRET_KEY}
    return str(requests.post(url, params=params).json().get("access_token"))


